const app = getApp()
var util = require('../../utils/util.js')
var wxCharts = require('../../data/wxcharts.js')
var lineChart = null

Page({

  /**
   * 页面的初始数据
   */
  data: {
    options:0,
    id:0,
    tmpcnt:2,
    price:0,
    clickable:false,
    merchandise: {
      "id": 0,
      "photopath": 'https://6d61-master-27262a-1259058618.tcb.qcloud.la/images/Diamond/%E6%9C%89%E9%92%B1-%E9%81%93%E8%B7%AF-%E8%B4%AB%E7%A9%B7_02.jpg?sign=84175aa5a9fe22746bcd36618efaa12b&t=1557232644',
      "name": '道路千万条，安全第一条',
      "type": 'new',
      "activity": 'liulangdiqiu',
      "price": 1,
      "number": 1000,
      "description": '武家伟于2019年2月28日软工课上挖出了这颗钻石'
    }
  },

  touchHandler: function (e) {
    lineChart.scrollStart(e);
  },

  moveHandler: function (e) {
    lineChart.scroll(e);
  },

  touchEndHandler: function (e) {
    lineChart.scrollEnd(e);
    lineChart.showToolTip(e, {
      format: function (item, category) {
        return category + '号 ' + item.name + ':' + item.data + '元'
      }
    });
  },

  goToBuy() {
    var _this = this
    _this.setData({
      clickable: true
    })
    var time = util.formatTime(new Date())
    var privKey = "";
    wx.request({
      url: 'https://diamondaction.internetapi.cn:8443/SCIDE/SCManager',
      data: {
        "action": "executeContract",
        "contractID": "DiamondOperation",
        "operation": "buy",
        "arg": "{\"cid\":" + _this.data.id + ", \"buyer\": 1, \"price\":" + _this.data.price + ", \"time\":\"" + time + "\"}",
        "privKey": privKey
      },
      dataType: "jsonp",
      success: function (res) {
        if (res.data) {
          wx.showToast({
            title: '购买成功',
            icon: 'success',
            duration: 2000
          })
        }
      }
    })
    _this.onLoad(_this.data.options)
  },

  updatecnt(callback) {
    var _this = this
    var privKey = "";
    wx.request({
      url: 'https://diamondaction.internetapi.cn:8443/SCIDE/SCManager',
      data: {
        "action": "executeContract",
        "contractID": "DiamondOperation",
        "operation": "diaCheckCnt",
        "arg": "{\"cid\":" + _this.data.id + "}",
        "privKey": privKey
      },
      dataType: "jsonp",
      success: function (res) {
        if (res.data) {
          _this.setData({
            tmpcnt: JSON.parse((JSON.parse(JSON.parse(res.data).data)).result),
            price: JSON.parse((JSON.parse(JSON.parse(res.data).data)).result) - 0.01
          })
          _this.data.merchandise.cnt = _this.data.tmpcnt
          _this.data.merchandise.price = _this.data.merchandise.cnt - 0.01

          var price = new Array(20).fill(0);
          var catagory = new Array(20).fill(0)
          for (var i = 0; i < 20; i++) {
            price[i] = _this.data.merchandise.cnt + i - 0.01;
          }
          for (var i = 0; i < 20; i++) {
            catagory[i] = _this.data.merchandise.cnt + i;
          }


          lineChart = new wxCharts({
            canvasId: 'lineGraph',
            type: 'line',
            categories: catagory,
            series: [{
              name: '',
              data: price
            }],
            yAxis: {
              title: '价格',
              min: 0
            },
            width: 400,
            height: 200,
            dataLabel: true,
            legend: false,
            dataPointShape: true,
            enableScroll: true,
            extra: {
              lineStyle: 'curve'
            }
          })
        }
      }
    })
  },

  /**
   * 生命周期函数--监听页面加载
   */
  onLoad: function (options) {
    console.log(app.goodsInfo)
    this.setData({
      options:options,
      clickable:false,
      id:options.id,
      merchandise:app.goodsInfo[options.id - 1]
    });

    // console.log(app.goodsInfo)

    // console.log(this.data.id)
    // console.log(this.data.merchandise)

    this.updatecnt()
  },

  /**
   * 生命周期函数--监听页面初次渲染完成
   */
  onReady: function () {

  },

  /**
   * 生命周期函数--监听页面显示
   */
  onShow: function () {

  },

  /**
   * 生命周期函数--监听页面隐藏
   */
  onHide: function () {

  },

  /**
   * 生命周期函数--监听页面卸载
   */
  onUnload: function () {

  },

  /**
   * 页面相关事件处理函数--监听用户下拉动作
   */
  onPullDownRefresh: function () {

  },

  /**
   * 页面上拉触底事件的处理函数
   */
  onReachBottom: function () {

  },

  /**
   * 用户点击右上角分享
   */
  onShareAppMessage: function () {

  }
})